INSERT INTO `fasp`.`ap_static_label`(`STATIC_LABEL_ID`,`LABEL_CODE`,`ACTIVE`) VALUES ( NULL,'static.dataEntryAndAdjustments.DesiredReview','1');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,1,'Select the forecast program and the desired review period. By default, QAT pre-selects a date range of 36 months prior to the start of the forecasting period. A non-editable table and all products related to this forecast program are displayed, along with their consumption data if you have previously entered that data;');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,2,'Sélectionnez le programme prévisionnel et la période de révision souhaitée. Par défaut, QAT présélectionne une plage de dates de 36 mois avant le début de la période de prévision. Un tableau non modifiable et tous les produits liés à ce programme prévisionnel sont affichés, ainsi que leurs données de consommation si vous avez préalablement saisi ces données ;');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,3,'Seleccione el programa de pronóstico y el período de revisión deseado. De forma predeterminada, QAT preselecciona un intervalo de fechas de 36 meses antes del inicio del período de previsión. Se muestra una tabla no editable y todos los productos relacionados con este programa de pronóstico, junto con sus datos de consumo si ha ingresado previamente esos datos;');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,4,'Selecione o programa de previsão e o período de revisão desejado. Por padrão, o QAT pré-seleciona um intervalo de datas de 36 meses antes do início do período de previsão. Uma tabela não editável e todos os produtos relacionados a este programa de previsão são exibidos, juntamente com seus dados de consumo, caso você tenha inserido esses dados anteriormente;');
