INSERT INTO `fasp`.`ap_static_label`(`STATIC_LABEL_ID`,`LABEL_CODE`,`ACTIVE`) VALUES ( NULL,'static.dataEntryAndAdjustments.ClickInterpolate','1');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,1,'Click the green "Interpolate" button in the middle right of the screen to search for periods where the consumption value is blank and replace them with an interpolated value. QAT interpolates by finding the nearest values on either side (before or after the blank), calculates the straight line in between them and uses that straight-line formula to calculate the value for the blank(s).');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,2,'Cliquez sur le bouton vert « Interpoler » au milieu à droite de lécran pour rechercher les périodes où la valeur de consommation est vide et les remplacer par une valeur interpolée. QAT interpole en trouvant les valeurs les plus proches de chaque côté (avant ou après le blanc), calcule la ligne droite entre eux et utilise cette formule linéaire pour calculer la valeur du ou des blancs.');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,3,'Haga clic en el botón verde "Interpolar" en el centro derecho de la pantalla para buscar períodos en los que el valor de consumo está en blanco y reemplazarlos con un valor interpolado. QAT interpola encontrando los valores más cercanos a cada lado (antes o después del espacio en blanco), calcula la línea recta entre ellos y usa esa fórmula de línea recta para calcular el valor de los espacios en blanco.');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,4,'Clique no botão verde "Interpolar" no meio direito da tela para procurar períodos em que o valor de consumo está em branco e substituí-los por um valor interpolado. QAT interpola encontrando os valores mais próximos em cada lado (antes ou depois do branco), calcula a linha reta entre eles e usa essa fórmula de linha reta para calcular o valor do(s) branco(s).');
INSERT INTO `fasp`.`ap_static_label`(`STATIC_LABEL_ID`,`LABEL_CODE`,`ACTIVE`) VALUES ( NULL,'static.dataEntryAndAdjustments.InterpolateForMonths','1');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,1,'Note that QAT will not interpolate for months where actual consumption is zero. QAT will only interpolate if there is at least one data point before and one data point after the blank value(s).');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,2,'Notez que le QAT neffectuera pas dinterpolation pour les mois où la consommation réelle est nulle. QAT ninterpolera que sil y a au moins un point de données avant et un point de données après la ou les valeurs à blanc.');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,3,'Tenga en cuenta que QAT no interpolará los meses en los que el consumo real sea cero. QAT solo interpolará si hay al menos un punto de datos antes y un punto de datos después de los valores en blanco.');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,4,'Observe que o QAT não fará a interpolação para meses em que o consumo real for zero. O QAT apenas interpolará se houver pelo menos um ponto de dados antes e um ponto de dados após o(s) valor(es) em branco.');
