INSERT INTO `fasp`.`ap_static_label`(`STATIC_LABEL_ID`,`LABEL_CODE`,`ACTIVE`) VALUES ( NULL,'static.extrapolation.TripleExponentialTextOne','1');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,1,'Raw data is usually spiky. In statistics, different types of smoothing are used to filter out the noise so that we can see patterns in a time series dataset more clearly.');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,2,'Les données brutes sont généralement hérissées. Dans les statistiques, différents types de lissage sont utilisés pour filtrer le bruit afin que nous puissions voir plus clairement les modèles dans un ensemble de données de séries chronologiques.');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,3,'Los datos sin procesar suelen ser puntiagudos. En las estadísticas, se utilizan diferentes tipos de suavizado para filtrar el ruido, de modo que podamos ver los patrones en un conjunto de datos de series temporales con mayor claridad.');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,4,'Os dados brutos geralmente são pontiagudos. Em estatística, diferentes tipos de suavização são usados para filtrar o ruído para que possamos ver padrões em um conjunto de dados de séries temporais com mais clareza.');
