INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,1,'Forecasts using triple exponential smoothing have three parameters. Exponential smoothing uses older data at exponentially decreasing weights over time.');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,2,'Les prévisions utilisant le triple lissage exponentiel ont trois paramètres. Le lissage exponentiel utilise des données plus anciennes à des poids décroissants de manière exponentielle au fil du temps.');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,3,'Los pronósticos que utilizan el suavizado exponencial triple tienen tres parámetros. El suavizado exponencial utiliza datos más antiguos con pesos que disminuyen exponencialmente a lo largo del tiempo.');
INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,4,'As previsões que usam suavização exponencial tripla têm três parâmetros. A suavização exponencial usa dados mais antigos com pesos exponencialmente decrescentes ao longo do tempo.');
INSERT INTO `fasp`.`ap_static_label`(`STATIC_LABEL_ID`,`LABEL_CODE`,`ACTIVE`) VALUES ( NULL,'static.extrapolation.TripleExponentialTextThree','1');
