INSERT INTO ap_static_label_languages VALUES(NULL,@MAX,3,'A menudo se aplica a pronósticos a corto plazo. Auto-regresivo significa que cada punto en la regresión está influenciado por sus valores anteriores y promedio móvil implica que cada punto es un promedio,');
